mapper.select:
select u.id, u.Userid, u.User_Pwd, u.User_Name, u.Phone_number, u.Email_address, u.Enabled_state, u.Sort_number, u.creater, u.created, u.modified,d.id d_id,d.folderName d_folderName from org_user u left join org_dept d on(u.fk_dept=d.id) where 1=1
and (u.userid like CONCAT(CONCAT('%',#{keyword}), '%') or u.User_Name like CONCAT(CONCAT('%', #{keyword}), '%') )
and u.fk_dept = #{deptId}
